  return "Loading";
}
const processData = (data) => {
  const accounts = Object.entries(data);
  const allItems = accounts
    .map((account) => {
      const accountId = account[0];
      return Object.entries(account[1].widget).map((kv) => ({
        accountId,
        widgetName: kv[0],
        blockHeight: kv[1],
      }));
    })
    .flat();
  allItems.sort((a, b) => b.blockHeight - a.blockHeight);
  return allItems;
};
