const InlineUser = ({ accountId }) => {
  const profile = Social.getr(`${accountId}/profile`);
  return (
    <div className="d-flex gap-1 align-items-center text-truncate">
      <Widget
        src="mob.near/widget/ProfileImage"
        props={{
          accountId,
          style: { width: 16, objectFit: "cover" },
