  align-items: center;
  gap: 8px;
  flex-grow: 1;
  color: var(--color) !important;
  font-family: var(--accent-font-family), InterVariable, sans-serif;
  font-weight: 500;
  font-size: 16px;
  border: 1px solid var(--stroke);
  transition: background 300ms;
  &:hover {
    text-decoration: none;
    cursor: pointer;
    background: var(--bg-hover);
  }
`;
return (
  <Card>
    <a
      style={{ textDecoration: "none" }}
      target="_blank"
      href={`https://near.social/${widgetPath}`}
    >
      <div className="d-flex gap-3 p-3 w-100 overflow-auto">
        <Widget
          src="mob.near/widget/Image"
          props={{
            image: image,
            style: {
              objectFit: "cover",
              borderRadius: "1em",
              width: 88,
              height: 88,
            },
            thumbnail: false,
            fallbackUrl:
              "https://ipfs.near.social/ipfs/bafkreido7gsk4dlb63z3s5yirkkgrjs2nmyar5bxyet66chakt2h5jve6e",
            alt: widgetName,
          }}
        />
        <div className="d-flex flex-column gap-1">
          <p
            className="m-0"
            style={{
              color: "var(--color)",
              fontSize: 20,
              fontWeight: 500,
              fontFamily:
                "var(--accent-font-family), InterVariable, sans-serif",
            }}
          >
            {name}
          </p>
          <span className="text-truncate">
            <InlineUser accountId={accountId} />
          </span>
          <p className="m-0">
