const { Root } = VM.require("bos.workspace/widget/Profile.Modern.Root") || {
  Root: () => <></>,
};
const { Button, LinkTree, Hashtag } = VM.require(
  "bos.workspace/widget/Components"
) || {
  Button: () => <></>,
  LinkTree: () => <></>,
  Hashtag: () => <></>,
};
const { SocialSDK } = VM.require("bos.workspace/widget/sdk.social") || {
  SocialSDK: {},
};
const { FollowStats } = VM.require(
  "bos.workspace/widget/Components.Profile.FollowStats"
) || {
  FollowStats: () => <></>,
};
const { LinkIcon, CopyIcon } = VM.require("bos.workspace/widget/Icons") || {
  LinkIcon: () => <></>,
  CopyIcon: () => <></>,
};
const accountId = props.accountId ?? context.accountId;
if (!accountId) {
  return (
    <div>
      <p>No user signed in or AccountId passed</p>
    </div>
  );
}
const profile = Social.getr(`${accountId}/profile`);
const profileTags = Object.keys(profile.tags || []);
const theme = profile.profileTheme ?? "light";
const font = profile.profileFont ?? "InterVariable";
const activeColor = profile.profileActiveColor ?? "#E93D82";
const ProfileImagesContainer = styled.div`
  position: relative;
  overflow-x: clip;
  overflow-y: visible;
  z-index: 5;
`;
const BackgroundImage = styled.div`
  img {
    z-index: 5;
    width: 100%;
    height: 280px;
    object-fit: cover;
    @media (max-width: 768px) {
      height: 120px;
    }
  }
`;
const ProfileImage = styled.div`
  position: absolute;
  bottom: 0;
  left: 50%;
  transform: translate(-50%, 50%);
  img {
    z-index: 5;
    width: 140px;
    height: 140px;
    object-fit: cover;
    border-radius: 100%;
    border: 3px solid var(--profile-stroke);
  }
  @media (max-width: 768px) {
    img {
      width: 80px;
      height: 80px;
    }
  }
`;
const ProfileName = styled.h2`
  color: var(--color);
  font-family: InterVariant, sans-serif;
  font-size: 40px;
  font-style: normal;
  font-weight: 600;
  line-height: 140%; /* 56px */
  letter-spacing: -0.4px;
  margin: 0;
  display: inline-flex;
  gap: 8px;
  align-items: center;
  svg {
    scale: 1.5;
    margin-bottom: 6px;
  }
`;
const AccountName = styled.div`
  display: inline-flex;
  gap: 4px;
  align-items: center;
  color: var(--color-muted);
  font-family: InterVariable;
  font-size: 16px;
  font-style: normal;
  font-weight: 400;
  line-height: 140%; /* 22.4px */
  span {
    cursor: pointer;
    margin-top: 3px;
  }
`;
const LeftFullBlur = styled.div`
  width: 407px;
  height: 697px;
  flex-shrink: 0;
  border-radius: 697px;
  background: var(--active-color);
  filter: blur(200px);
  position: absolute;
  left: 0;
  top: 0;
  transform: translateX(-50%);
  opacity: 0.4;
  pointer-events: none;
